const ccc = require("clear_concise_creative")

const automate_me = require("automate_me")

function makeGlobal() {
    let commands = ["mkdir dist", "touch playground.js", "touch README.md", "touch index.js", "npm init --y", ]
    for (let i = 0; i < commands.length; i++) {
        automate_me.runCommand(commands[i])
    }
}

function addIndex(fullstack = false) {
    if (fullstack === true) {
        let string = ccc.readFile("./dist/index.js")
        ccc.writeFile("../deploy/index.js", string)
    } else {
        let readFile = ccc.readFile("./index.js")
        ccc.writeFile("../deploy/index.js", readFile)
        ccc.copy("./dist/**.js", "../deploy/", true)
    }
}

module.exports = { makeGlobal, addIndex }